import React from 'react';
import { Card } from 'react-bootstrap';

const Product = (props) => {
  const { item } = props;

  return (
    <div>
      <Card className='my-3 p-4 rounded'>
        <a href={`/product/${item._id}`}>
          <Card.Img src={item.image} />
        </a>
        <Card.Body>
          <a href={`/product/${item._id}`}>
            <Card.Title>
              <strong>item.name</strong>
            </Card.Title>
          </a>
        </Card.Body>
      </Card>
    </div>
  );
};

export default Product;
