import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Card, Button } from 'react-bootstrap';
import Rating from '../components/Rating.js';
import { itemProduct } from '../store/actions/productByIdAction.js';

const ProductScreen = ({ match }) => {
  const dispatch = useDispatch();
  const itemList = useSelector((state) => state.itemProduct);
  useEffect(() => {
    dispatch(itemProduct(match.params.id));
  }, [dispatch]);

  return (
    <>
      <Link className='btn btn-dark my-3' to='/'>
        {' '}
        Home Page
      </Link>
      <Row>
        <Col md={3}>
          <Image src={itemList.image} alt={itemList.name} fluid />
        </Col>
        <Col md={4}>
          <ListGroup variant='flush'>
            <ListGroup.Item>
              <h3>{itemList.name}</h3>
            </ListGroup.Item>
            <ListGroup.Item>
              <Rating
                value={itemList.rating}
                text={`${itemList.numReviews} reviews`}
              />
            </ListGroup.Item>
            <ListGroup.Item>price : ${itemList.price}</ListGroup.Item>
            <ListGroup.Item>
              Description : {itemList.description}
            </ListGroup.Item>
          </ListGroup>
        </Col>
        <Col>
          <Card>
            <ListGroup.Item>
              <Row>
                <Col> price:</Col>
                <Col>
                  <strong>${itemList.price}</strong>
                </Col>
              </Row>
            </ListGroup.Item>
            <ListGroup.Item>
              <Row>
                <Col>status:</Col>
                <Col>
                  {itemList.countInStock > 1 ? 'In stock' : 'out of stock'}
                </Col>
              </Row>
            </ListGroup.Item>
            <ListGroup.Item>
              <Row>
                <Button
                  className='btn-black'
                  type='button'
                  disabled={itemList.countInStock === 0}
                >
                  Add TO Cart
                </Button>
              </Row>
            </ListGroup.Item>
          </Card>
        </Col>
      </Row>
    </>
  );
};
export default ProductScreen;
