import React from 'react';

const Rating = (props) => {
  const { value, text } = props;
  let rating = [0.5, 1.5, 2.5, 3.5, 4.5];
  return (
    <div className='rating'>
      <samp>
        {rating.forEach((star, index) => {
          <i
            className={
              value >= index
                ? 'fas fa-star'
                : value >= star
                ? 'fas fa-star-half-alt'
                : 'far fa-star'
            }
          ></i>;
        })}
      </samp>
    </div>
  );
};

export default Rating;
