import React from 'react';
import { Card } from 'react-bootstrap';

const Product = (props) => {
  const { item } = props;

  return (
    <div>
      <Card className='my-3 p-3rounded'>
        <a href={`/product/${item._id}`}>
          <Card.Img src={item.image} />
        </a>
      </Card>
    </div>
  );
};

export default Product;
