import { listProducts } from '../store/productAction.js';

const HomeScreen = () => {
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(listProducts);
  }, [dispatch]);

  return (
    <div>
      <h1>Latest Products</h1>
      <Row>
        {products.map((item, index) => (
          <Col key={index} sm={12} md={4} lg={4} xl={3}>
            <Product item={item} />
          </Col>
        ))}
      </Row>
    </div>
  );
};

