import axios from 'axios';
import { PRODUCT_LIST_BY_ID } from '../constants/productConstant.js';

export const itemProduct = async (dispatch, match) => {
  let result = await axios.get(`/api/products/${match.params.id}`);

  dispatch({
    type: PRODUCT_LIST_BY_ID,
    payload: result.data,
  });
};
