import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import Message from '../components/Message.js';
import {
  Row,
  Col,
  ListGroup,
  Image,
  Form,
  Button,
  Card,
} from 'react-bootstrap';

import { cartAction } from '../store/actions/cartAction.js';

const CartScreen = ({ match, location }) => {
  const productId = match.params.id;
  const dispatch = useDispatch();
  const cartList = useSelector((state) => state.cartList);

  console.log(cartList.cartCollection);
  let qty = location.search ? Number(location.search.split('=')[1]) : 1;
  useEffect(() => {
    dispatch(cartAction(productId, qty));
  }, [dispatch, qty, productId]);

  return (
    <div>
      <Col md={8}>
        <h1>Shopping Cart</h1>
        {cartList.cartCollection === 0 ? (
          <Message>your cart is empty go Back</Message>
        ) : (
          cartList.cartCollection
        )}
      </Col>
      <Col md={2}></Col>
    </div>
  );
};
export default CartScreen;
